import React, { useState } from 'react'
import AddIcon from '@material-ui/icons/Add';

const CreateNote = () =>{

    const [expand , setExpand] = useState(false);

    const expandEvent = () =>{
        setExpand(true);
    }
    const btNormal = () =>{
        setExpand(false);
    }

    return (
       <>
       <div className="container" onDoubleClick={btNormal}>
       <div className="main">
       <form>
         { expand ?
           <input 
           type="text"
           placeholder="Title"
           autoComplete="off" />
            : null}
           <br />
           <textarea
            rows=''
            column=''
            placeholder='Take a note.....'
            onClick={expandEvent} />
           <br />
           {
            expand?
           <button>
           <AddIcon className="plus_sign" />
           </button> : null
           }
       </form>
       </div>
       </div>
       </> 
    );
};

export default CreateNote;
